'use client'

import React from 'react'

// Event Type Badge Component
const EventBadge = ({ type }) => {
  const badgeStyles = {
    S: 'bg-[#0E3A2F] border border-[#78FAAE] text-[#78FAAE]',
    E: 'bg-[#0E3A2F] border border-[#FAEB67] text-[#FAEB67]',
    B: 'bg-[#0E3A2F] border border-[#1ED4DF] text-[#1ED4DF]',
    W: 'bg-[#0E3A2F] border border-[#F7B046] text-[#F7B046]',
  }

  return (
    <div className={`w-[30px] px-2 py-1 rounded-[1px] flex items-center justify-center ${badgeStyles[type]}`}>
      <span className="text-[16px] font-bold leading-[22px]">
        {type}
      </span>
    </div>
  )
}

// Event Time Component
const EventTime = ({ time, speaker, bold = false }) => {
  return (
    <div className="text-[#78FAAE] text-[14px] font-sans leading-[21px]">
      {time}
      {speaker && (
        <>
          <br />
          <span className={bold ? 'font-bold' : 'font-normal'}>{speaker}</span>
        </>
      )}
    </div>
  )
}

// Event Row Component
const EventRow = ({ time, speaker, title, badge, highlighted = false, border = 'normal' }) => {
  const backgroundClass = highlighted ? 'bg-[#194D3C]' : 'bg-transparent'
  const borderClass = border === 'normal' ? 'border-b-2 border-[#7F7F7F]' : 'border-b-2 border-[#788E87]'

  return (
    <div className={`w-full px-[10px] py-[10px] flex justify-start items-start gap-2 ${backgroundClass} ${borderClass}`}>
      <div className="flex-shrink-0 min-w-[120px]">
        <EventTime time={time} speaker={speaker} bold={!!speaker} />
      </div>
      <div className="flex-grow text-[#78FAAE] text-[14px] font-sans font-normal leading-[21px]">
        {title}
      </div>
      {badge && (
        <div className="flex-shrink-0">
          <EventBadge type={badge} />
        </div>
      )}
    </div>
  )
}

// Section Header Component
const SectionHeader = ({ title }) => {
  return (
    <div className="w-full px-4 py-2 mt-[44px] mb-[44px]">
      <h2 className="text-[#78FAAE] text-[20px] font-sans font-bold leading-[21px]">
        {title}
      </h2>
    </div>
  )
}

